// EDDSA
pub fn eddsa_pubkey_wallet1() -> String {
    let shard1: KeygenResult = serde_json::from_str(wallet1_shard1().as_str()).unwrap();
    hex::encode(&shard1.eddsa.local_key.agg_pubkey.to_bytes(true).to_vec())
}

pub fn eddsa_sign_wallet1(msg: &str) -> SigningState {
    let shard1: KeygenResult = serde_json::from_str(wallet1_shard1().as_str()).unwrap();
    let shard2: KeygenResult = serde_json::from_str(wallet1_shard2().as_str()).unwrap();

    let mut state = SigningState {
        t: 1,
        n: 3,
        signing_parts: vec![],
        signature: None,
    };
    let message_to_sign = hex::decode(msg).unwrap();
    t_ed25519::signing::sign(&mut state, &shard1.eddsa, message_to_sign.clone(), 1, 0).unwrap();
    t_ed25519::signing::sign(&mut state, &shard2.eddsa, message_to_sign, 2, 0).unwrap();
    state
}

