// ECDSA

pub fn ecdsa_pubkey_wallet1() -> String {
    let shard1: KeygenResult = serde_json::from_str(wallet1_shard1().as_str()).unwrap();
    hex::encode(&shard1.ecdsa.local_key.public_key().to_bytes(true).to_vec())
}

pub fn ecdsa_sign_wallet1(msg: &str) -> SigningState {
    let shard1: KeygenResult = serde_json::from_str(wallet1_shard1().as_str()).unwrap();
    let shard2: KeygenResult = serde_json::from_str(wallet1_shard2().as_str()).unwrap();

    let mut state = SigningState {
        t: 1,
        n: 3,
        signing_parts: vec![],
        signature: None,
    };
    let message_to_sign = hex::decode(msg).unwrap();
    gg20::signing::sign(
        &mut state,
        &shard2.ecdsa,
        message_to_sign.clone(),
        2,
        vec![1, 2],
    )
    .unwrap();
    gg20::signing::sign(&mut state, &shard1.ecdsa, message_to_sign, 1, vec![1, 2]).unwrap();
    state
}

pub fn ecdsa_pubkey_wallet2() -> String {
    let shard1: KeygenResult = serde_json::from_str(wallet2_shard1().as_str()).unwrap();
    hex::encode(&shard1.ecdsa.local_key.public_key().to_bytes(true).to_vec())
}

pub fn ecdsa_sign_wallet2(msg: &str) -> SigningState {
    let shard1: KeygenResult = serde_json::from_str(wallet2_shard1().as_str()).unwrap();
    let shard2: KeygenResult = serde_json::from_str(wallet2_shard2().as_str()).unwrap();

    let mut state = SigningState {
        t: 1,
        n: 3,
        signing_parts: vec![],
        signature: None,
    };
    let message_to_sign = hex::decode(msg).unwrap();
    gg20::signing::sign(
        &mut state,
        &shard1.ecdsa,
        message_to_sign.clone(),
        1,
        vec![1, 2],
    )
    .unwrap();
    gg20::signing::sign(&mut state, &shard2.ecdsa, message_to_sign, 2, vec![1, 2]).unwrap();
    state
}
